     1→import { useEffect, useState } from "react";
     2→import { Toaster } from "sonner";
     3→import "./App.css";
     4→import AccessibilityPermissions from "./components/AccessibilityPermissions";
     5→import Footer from "./components/footer";
     6→import Onboarding from "./components/onboarding";
     7→import { Sidebar, SidebarSection, SECTIONS_CONFIG } from "./components/Sidebar";
     8→import { useSettings } from "./hooks/useSettings";
     9→import { commands } from "@/bindings";
    10→
    11→const renderSettingsContent = (section: SidebarSection) => {
    12→  const ActiveComponent =
    13→    SECTIONS_CONFIG[section]?.component || SECTIONS_CONFIG.general.component;
    14→  return <ActiveComponent />;
    15→};
    16→
    17→function App() {
    18→  const [showOnboarding, setShowOnboarding] = useState<boolean | null>(null);
    19→  const [currentSection, setCurrentSection] =
    20→    useState<SidebarSection>("general");
    21→  const { settings, updateSetting } = useSettings();
    22→
    23→  useEffect(() => {
    24→    checkOnboardingStatus();
    25→  }, []);
    26→
    27→  // Handle keyboard shortcuts for debug mode toggle
    28→  useEffect(() => {
    29→    const handleKeyDown = (event: KeyboardEvent) => {
    30→      // Check for Ctrl+Shift+D (Windows/Linux) or Cmd+Shift+D (macOS)
    31→      const isDebugShortcut =
    32→        event.shiftKey &&
    33→        event.key.toLowerCase() === "d" &&
    34→        (event.ctrlKey || event.metaKey);
    35→
    36→      if (isDebugShortcut) {
    37→        event.preventDefault();
    38→        const currentDebugMode = settings?.debug_mode ?? false;
    39→        updateSetting("debug_mode", !currentDebugMode);
    40→      }
    41→    };
    42→
    43→    // Add event listener when component mounts
    44→    document.addEventListener("keydown", handleKeyDown);
    45→
    46→    // Cleanup event listener when component unmounts
    47→    return () => {
    48→      document.removeEventListener("keydown", handleKeyDown);
    49→    };
    50→  }, [settings?.debug_mode, updateSetting]);
    51→
    52→  const checkOnboardingStatus = async () => {
    53→    try {
    54→      // Always check if they have any models available
    55→      const result = await commands.hasAnyModelsAvailable();
    56→      if (result.status === "ok") {
    57→        setShowOnboarding(!result.data);
    58→      } else {
    59→        setShowOnboarding(true);
    60→      }
    61→    } catch (error) {
    62→      console.error("Failed to check onboarding status:", error);
    63→      setShowOnboarding(true);
    64→    }
    65→  };
    66→
    67→  const handleModelSelected = () => {
    68→    // Transition to main app - user has started a download
    69→    setShowOnboarding(false);
    70→  };
    71→
    72→  if (showOnboarding) {
    73→    return <Onboarding onModelSelected={handleModelSelected} />;
    74→  }
    75→
    76→  return (
    77→    <div className="h-screen flex flex-col">
    78→      <Toaster />
    79→      {/* Main content area that takes remaining space */}
    80→      <div className="flex-1 flex overflow-hidden">
    81→        <Sidebar
    82→          activeSection={currentSection}
    83→          onSectionChange={setCurrentSection}
    84→        />
    85→        {/* Scrollable content area */}
    86→        <div className="flex-1 flex flex-col overflow-hidden">
    87→          <div className="flex-1 overflow-y-auto">
    88→            <div className="flex flex-col items-center p-4 gap-4">
    89→              <AccessibilityPermissions />
    90→              {renderSettingsContent(currentSection)}
    91→            </div>
    92→          </div>
    93→        </div>
    94→      </div>
    95→      {/* Fixed footer at bottom */}
    96→      <Footer />
    97→    </div>
    98→  );
    99→}
   100→
   101→export default App;
   102→

