   155→        let _ = self.app_handle.emit(
   156→            "model-state-changed",
   157→            ModelStateEvent {
   158→                event_type: "unloaded".to_string(),
   159→                model_id: None,
   160→                model_name: None,
   161→                error: None,
   162→            },
