    10→fn set_mute(mute: bool) {
    11→    // Expected behavior:
    12→    // - Windows: works on most systems using standard audio drivers.
    13→    // - Linux: works on many systems (PipeWire, PulseAudio, ALSA),
    14→    //   but some distros may lack the tools used.
    15→    // - macOS: works on most standard setups via AppleScript.
    16→    // If unsupported, fails silently.
    17→
    18→    #[cfg(target_os = "windows")]
    19→    {
    20→        unsafe {
    21→            use windows::Win32::{
    22→                Media::Audio::{
    23→                    eMultimedia, eRender, Endpoints::IAudioEndpointVolume, IMMDeviceEnumerator,
    24→                    MMDeviceEnumerator,
    25→                },
    26→                System::Com::{CoCreateInstance, CoInitializeEx, CLSCTX_ALL, COINIT_MULTITHREADED},
    27→            };
    28→
    29→            macro_rules! unwrap_or_return {
    30→                ($expr:expr) => {
    31→                    match $expr {
    32→                        Ok(val) => val,
    33→                        Err(_) => return,
    34→                    }
    35→                };
    36→            }
    37→
    38→            // Initialize the COM library for this thread.
    39→            // If already initialized (e.g., by another library like Tauri), this does nothing.
    40→            let _ = CoInitializeEx(None, COINIT_MULTITHREADED);
    41→
    42→            let all_devices: IMMDeviceEnumerator =
    43→                unwrap_or_return!(CoCreateInstance(&MMDeviceEnumerator, None, CLSCTX_ALL));
    44→            let default_device =
    45→                unwrap_or_return!(all_devices.GetDefaultAudioEndpoint(eRender, eMultimedia));
    46→            let volume_interface = unwrap_or_return!(
    47→                default_device.Activate::<IAudioEndpointVolume>(CLSCTX_ALL, None)
    48→            );
    49→
    50→            let _ = volume_interface.SetMute(mute, std::ptr::null());
    51→        }
    52→    }
    53→
    54→    #[cfg(target_os = "linux")]
    55→    {
    56→        use std::process::Command;
    57→
    58→        let mute_val = if mute { "1" } else { "0" };
    59→        let amixer_state = if mute { "mute" } else { "unmute" };
    60→
    61→        // Try multiple backends to increase compatibility
    62→        // 1. PipeWire (wpctl)
    63→        if Command::new("wpctl")
    64→            .args(["set-mute", "@DEFAULT_AUDIO_SINK@", mute_val])
    65→            .output()
    66→            .map(|o| o.status.success())
    67→            .unwrap_or(false)
    68→        {
    69→            return;
    70→        }
    71→
    72→        // 2. PulseAudio (pactl)
    73→        if Command::new("pactl")
    74→            .args(["set-sink-mute", "@DEFAULT_SINK@", mute_val])
    75→            .output()
    76→            .map(|o| o.status.success())
    77→            .unwrap_or(false)
    78→        {
    79→            return;
    80→        }
    81→
    82→        // 3. ALSA (amixer)
    83→        let _ = Command::new("amixer")
    84→            .args(["set", "Master", amixer_state])
    85→            .output();
    86→    }
    87→
    88→    #[cfg(target_os = "macos")]
    89→    {
    90→        use std::process::Command;
    91→        let script = format!(
    92→            "set volume output muted {}",
    93→            if mute { "true" } else { "false" }
    94→        );
    95→        let _ = Command::new("osascript").args(["-e", &script]).output();
    96→    }
    97→}
