   117→fn create_audio_recorder(
   118→    vad_path: &str,
   119→    app_handle: &tauri::AppHandle,
   120→) -> Result<AudioRecorder, anyhow::Error> {
   121→    let silero = SileroVad::new(vad_path, 0.3)
   122→        .map_err(|e| anyhow::anyhow!("Failed to create SileroVad: {}", e))?;
   123→    let smoothed_vad = SmoothedVad::new(Box::new(silero), 15, 15, 2);
   124→
   125→    // Recorder with VAD plus a spectrum-level callback that forwards updates to
   126→    // the frontend.
   127→    let recorder = AudioRecorder::new()
   128→        .map_err(|e| anyhow::anyhow!("Failed to create AudioRecorder: {}", e))?
   129→        .with_vad(Box::new(smoothed_vad))
   130→        .with_level_callback({
   131→            let app_handle = app_handle.clone();
   132→            move |levels| {
   133→                utils::emit_levels(&app_handle, &levels);
   134→            }
   135→        });
   136→
   137→    Ok(recorder)
   138→}
