    49→pub fn change_binding(
    50→    app: AppHandle,
    51→    id: String,
    52→    binding: String,
    53→) -> Result<BindingResponse, String> {
    54→    let mut settings = settings::get_settings(&app);
    55→
    56→    // Get the binding to modify
    57→    let binding_to_modify = match settings.bindings.get(&id) {
    58→        Some(binding) => binding.clone(),
    59→        None => {
    60→            let error_msg = format!("Binding with id '{}' not found", id);
    61→            warn!("change_binding error: {}", error_msg);
    62→            return Ok(BindingResponse {
    63→                success: false,
    64→                binding: None,
    65→                error: Some(error_msg),
    66→            });
    67→        }
    68→    };
    69→    // If this is the cancel binding, just update the settings and return
    70→    // It's managed dynamically, so we don't register/unregister here
    71→    if id == "cancel" {
    72→        if let Some(mut b) = settings.bindings.get(&id).cloned() {
    73→            b.current_binding = binding;
    74→            settings.bindings.insert(id.clone(), b.clone());
    75→            settings::write_settings(&app, settings);
    76→            return Ok(BindingResponse {
    77→                success: true,
