     1→//! Meeting session management for Meeting Mode.
     2→//!
     3→//! This module provides the core data structures for meeting sessions,
     4→//! which are completely separate from the existing Quick Dictation functionality.
     5→
     6→use serde::{Deserialize, Serialize};
     7→use specta::Type;
     8→
     9→/// Represents the lifecycle status of a meeting session.
    10→///
    11→/// The state machine follows this flow:
    12→/// - Idle -> Recording (start meeting)
    13→/// - Recording -> Processing (stop meeting, begin transcription)
    14→/// - Processing -> Completed (transcription success)
    15→/// - Processing -> Failed (transcription failure)
    16→/// - Failed -> Processing (retry transcription)
    17→#[derive(Clone, Debug, Serialize, Deserialize, Type, PartialEq, Eq)]
    18→#[serde(rename_all = "snake_case")]
    19→pub enum MeetingStatus {
    20→    /// No active meeting session
    21→    Idle,
    22→    /// Meeting is currently being recorded
    23→    Recording,
    24→    /// Recording stopped, transcription in progress
    25→    Processing,
    26→    /// Meeting completed successfully with transcript
    27→    Completed,
    28→    /// Meeting failed (e.g., transcription error), audio preserved
    29→    Failed,
    30→}
    31→
    32→impl Default for MeetingStatus {
    33→    fn default() -> Self {
    34→        MeetingStatus::Idle
    35→    }
    36→}
    37→
    38→/// Represents a meeting session with its metadata and file references.
    39→///
    40→/// Each meeting session has a unique ID and is stored in a dedicated folder
    41→/// under the app's data directory: `{app_data}/meetings/{session-id}/`
    42→#[derive(Clone, Debug, Serialize, Deserialize, Type)]
    43→pub struct MeetingSession {
    44→    /// Unique identifier for the session (UUID format)
    45→    pub id: String,
    46→
    47→    /// User-editable title, defaults to timestamp format like
    48→    /// "Meeting - January 15, 2025 3:30 PM"
    49→    pub title: String,
    50→
    51→    /// Unix timestamp (seconds) when the meeting was created/started
    52→    pub created_at: i64,
    53→
    54→    /// Duration of the recording in seconds (set after recording stops)
    55→    pub duration: Option<i64>,
    56→
    57→    /// Current status of the meeting session
    58→    pub status: MeetingStatus,
    59→
    60→    /// Relative path to the audio file within the meetings directory
    61→    /// e.g., "{session-id}/audio.wav"
    62→    pub audio_path: Option<String>,
    63→
    64→    /// Relative path to the transcript file within the meetings directory
    65→    /// e.g., "{session-id}/transcript.txt"
