   118→    /// Migrate from tauri-plugin-sql's migration tracking to rusqlite_migration's.
   119→    /// tauri-plugin-sql used a _sqlx_migrations table, while rusqlite_migration uses
   120→    /// SQLite's user_version pragma. This function checks if the old system was in use
   121→    /// and sets the user_version accordingly so migrations don't re-run.
   122→    fn migrate_from_tauri_plugin_sql(&self, conn: &Connection) -> Result<()> {
   123→        // Check if the old _sqlx_migrations table exists
   124→        let has_sqlx_migrations: bool = conn
   125→            .query_row(
   126→                "SELECT COUNT(*) > 0 FROM sqlite_master WHERE type='table' AND name='_sqlx_migrations'",
   127→                [],
   128→                |row| row.get(0),
   129→            )
   130→            .unwrap_or(false);
   131→
   132→        if !has_sqlx_migrations {
   133→            return Ok(());
   134→        }
   135→
   136→        // Check current user_version
   137→        let current_version: i32 =
   138→            conn.pragma_query_value(None, "user_version", |row| row.get(0))?;
   139→
   140→        if current_version > 0 {
