   142→            return Ok(());
   143→        }
   144→
   145→        // Get the highest version from the old migrations table
   146→        let old_version: i32 = conn
   147→            .query_row(
   148→                "SELECT COALESCE(MAX(version), 0) FROM _sqlx_migrations WHERE success = 1",
   149→                [],
   150→                |row| row.get(0),
   151→            )
   152→            .unwrap_or(0);
   153→
   154→        if old_version > 0 {
   155→            info!(
   156→                "Migrating from tauri-plugin-sql (version {}) to rusqlite_migration",
   157→                old_version
   158→            );
   159→
   160→            // Set user_version to match the old migration state
   161→            conn.pragma_update(None, "user_version", old_version)?;
