   163→            // Optionally drop the old migrations table (keeping it doesn't hurt)
   164→            // conn.execute("DROP TABLE IF EXISTS _sqlx_migrations", [])?;
   165→
   166→            info!(
   167→                "Migration tracking converted: user_version set to {}",
   168→                old_version
   169→            );
