   157→    fn build_stream<T>(
   158→        device: &cpal::Device,
   159→        config: &cpal::SupportedStreamConfig,
   160→        sample_tx: mpsc::Sender<Vec<f32>>,
   161→        channels: usize,
   162→    ) -> Result<cpal::Stream, cpal::BuildStreamError>
   163→    where
   164→        T: Sample + SizedSample + Send + 'static,
   165→        f32: cpal::FromSample<T>,
   166→    {
   167→        let mut output_buffer = Vec::new();
   168→
   169→        let stream_cb = move |data: &[T], _: &cpal::InputCallbackInfo| {
   170→            output_buffer.clear();
   171→
   172→            if channels == 1 {
   173→                // Direct conversion without intermediate Vec
   174→                output_buffer.extend(data.iter().map(|&sample| sample.to_sample::<f32>()));
   175→            } else {
   176→                // Convert to mono directly
   177→                let frame_count = data.len() / channels;
   178→                output_buffer.reserve(frame_count);
   179→
   180→                for frame in data.chunks_exact(channels) {
   181→                    let mono_sample = frame
   182→                        .iter()
   183→                        .map(|&sample| sample.to_sample::<f32>())
   184→                        .sum::<f32>()
   185→                        / channels as f32;
   186→                    output_buffer.push(mono_sample);
   187→                }
   188→            }
   189→
   190→            if sample_tx.send(output_buffer.clone()).is_err() {
   191→                log::error!("Failed to send samples");
   192→            }
   193→        };
   194→
   195→        device.build_input_stream(
   196→            &config.clone().into(),
   197→            stream_cb,
   198→            |err| log::error!("Stream error: {}", err),
   199→            None,
   200→        )
   201→    }
